class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();

        vector<int> ones;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                ones.push_back(i);
            }
        }

        int n1=ones.size();
        vector<int> dp(n1);
        int sum=0;
        for(int i=n1-1;i>=0;i--){
            int cnt=0;
            if(i==n1-1){
                cnt=n-1-ones[i];
            }
            else{
                cnt=(ones[i+1]-ones[i]-1);
            }
            dp[i]=((i<n1-1)?dp[i+1]:0)+(cnt!=0);
            // cout<<dp[i]<<endl;
            sum+=dp[i];
        }
        return sum;
    }
};
