class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int o = 0;
        int ans = 0;
        int i = 0, j = 0;
         while (i < n) {
             if(s[i] == '0') {
                 j = i;
                while(j < n && s[j] == '0') {
                    j++;
                    }
                 ans += o;
            }
             if (s[i] == '1') {o++; i++; }
             else i = j;

         }

        return ans;
    }
};
