class Solution {
public:
    int maxOperations(string s) {
        int ret = 0;
        int count = 0;
        for(int i = 0; i < s.length(); ++i){
            if(s[i]=='0') continue;
            if(i > 0 && s[i]!=s[i-1]) ret += count;
            count++;
        }

        if(s.back()!='1') ret += count;
        return ret;
    }
};
