class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int p=0;
        int ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1')p++;
            if(s[i]=='0'){
                ans+=p;
                while(i<n && s[i]=='0')i++;
                i--;
            }


        }
        return ans;

    }
};
