class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        last_index = n-1
        count = 0
        num_0 = 0
        result = 0
        for i in range(n-1, -1, -1):
            if s[i] == '1':
                if num_0 > 0:
                    count += 1
                num_0 = 0
                result += count
            else:
                num_0 += 1

        return result
