class Solution {
public:
    int maxOperations(string s) {
        int i = 0;
        int c = 0;
        int ans = 0;
        while(i<s.size())
        {
            if(s[i] == '1')
            {
                c++;
                i++;
            }
            else if(s[i] == '0')
            {
                int j = i;
                while(j<s.size() && s[j] != '1')
                {
                    j++;
                }
                ans += c;
                i = j;
            }
        }
        return ans;
    }
};
