class Solution {
public:
    int maxOperations(string s) {
        int ans{};

        int nOf1{};
        for(int i{}; i< s.size(); i++){
            if(s[i] == '1') nOf1++;
            else {
                ans += nOf1;
                while(i< s.size()-1  && s[i+1] == '0') i++;
            }
        }

        return ans;
    }
};
