using ll = long long;
class Solution {
public:
  ll maxOperations(string s) {
    int n = s.length();
    int ones = 0;
    ll res = 0;
    bool ok = true;
    for(int i = 0; i < n; i++) {
      if(s[i] == '0') {
        if(ok) {
          res += ones;
          ok = false;
        }
      } else {
        ones++;
        ok = true;
      }
    }
    return res;
  }
};
