class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ind=0;
        int ans=0;
        int flag=1;
        int temp=0;
        while(ind<n) {
            if(s[ind]=='0') {
                flag=0;
                ans+=temp;
                while(ind<n and s[ind]=='0') {
                    ind++;
                }
                flag=1;

            }
            else {

                temp++;
                ind++;
            }
        }
        return ans;
    }
};
