class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int ones = 0, i = 0;
        while(i < s.length()){
            if(s[i] == '0'){
                i++;
                continue;
            }
            ans += ones;
            while(i < s.length() && s[i] == '1'){
                ones++;
                i++;
            }
        }
        if(s.back() == '0') ans += ones;
        return ans;
    }
};
