class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(),cnt=0,ans=0;
        bool bv = false;
        for(auto&a:s){
            if(a=='1')
                cnt++,
                bv = true;
            else if(bv)
                ans += cnt,
                bv = false;
        }
        return ans;
    }
};
