class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, ans = 0, n = s.size();
        for(int i = n-1; i >= 0; i--) {
            if(s[i] == '1' && s[i+1] == '0') {
                cnt++;
            }
            if(s[i] == '1')
                ans += cnt;
        } 
        return ans;
    }
};
