class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int count=0;
        bool one=false;
        bool zero=false;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                if(zero)
                {
                    ans+=count;
                    zero=false;
                    count++;
                }
                else
                {
                    count++;
                    one=true;
                }
            }
            else
            {
                if(one)
                {
                    zero=true;
                }
            }
        }
        if(zero) ans+=count;
        return ans;
    }
};
