class Solution:
    def maxOperations(self, s: str) -> int:
        one_count = 0
        i = 0
        total = 0
        while i < len(s) and s[i] == '0':
            i += 1
        while i < len(s):
            if s[i] == '1':
                one_count += 1
                i += 1
            else:
                total += one_count
                while i < len(s) and s[i] == '0':
                    i += 1
        return total
