class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int cmov = 0;
        bool flag = false;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '1') {
                // move to the right until can't move
                ++cmov;
                flag = 1;
            } else {
                if(flag) {
                    cnt += cmov;
                    flag = false;
                }                
            }
        }

        return cnt;
    }
};

