class Solution:
    def maxOperations(self, s: str) -> int:
        c = 0
        res = 0
        z = 0
        for i in s:
            if i == '1':
                if z:
                    res += c
                z = 0
                c += 1
            else:
                z += 1
        if s[-1] == '0':
            res += c

        return res
