class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        vector<int> v(n+1);
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0'&&(i==n-1||s[i+1]=='1'))
                v[i]++;
            v[i]+=v[i+1];
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1')
            ans+=v[i];
        }
        return ans;
    }
};
