class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int ans = 0;
        int i = 0, n = s.size();
        while (i < n){
            while (i < n && s[i] == '1'){
                ones++;
                i++;
            }
            bool found = false;
            while (i < n && s[i] == '0'){
                found = true;
                i++;
            }
            if (found){
                ans += ones;
            }
        }
        return ans;
    }
};
