class Solution:
    def maxOperations(self, s: str) -> int:
        cnt_1 = 0
        result = 0
        cnt_flag = True
        for idx in range(len(s)):
            if s[idx] == "1":
                cnt_1 += 1
                cnt_flag = True

            else:
                if cnt_flag:
                    cnt_flag = False
                    result += cnt_1

        return result
