class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int res = 0;
        int cnt = 0;
        for(int i = 0; i + 1 < n; i ++) {
            if(s[i] == '1' and s[i + 1] == '0') {
                res ++;
                res += cnt;
            }
            if(s[i] == '1') cnt ++;
        }
        return res;
    }
};
