class Solution {
public:
    int maxOperations(string s) {
        const int n = s.size();
        int groupCnt = 0;
        int res = 0;
        bool inGroup = false;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                inGroup = true;
                groupCnt++;
            } else {
                if (inGroup) {
                    inGroup = false;
                    res += groupCnt;
                }
            }
        }
        return res;
    }
};
