class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ans = 0  # 间隔 0 的个数
        cnt = 0  # 当前1的个数
        for i in range(n):
            if s[i] == '1':
                cnt += 1
            else:
                if i > 0 and s[i - 1] == '0':
                    continue
                ans += cnt
        return ans
