class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        s = list(s)
        n = len(s)
        cnt = 0
        pre = "1"
        for i in range(n-1, -1, -1):
            if s[i] == "0":
                if pre != s[i]:
                    cnt += 1
                pre = s[i]
            else:
                ans += cnt
                pre = s[i]
        return ans
