class Solution:
    def maxOperations(self, s: str) -> int:
        a=[i for i in range(len(s)) if s[i]=='1']
        i=0
        ans=0
        if len(a)==0:
            return 0
        for i in range(1,len(a)):
            if a[i-1]+1!=a[i]:
                ans+=i
        if a[-1]!=len(s)-1:
            ans+=len(a)
        return ans
