class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int one = 0;
        for(int i=0;i<s.length();i++){
            if(s.charAt(i) == '0'){
                while(i < s.length() && s.charAt(i) == '0') i++;
                i--;
                ans += one;
            }
            else{
                one++;
            } 
        }
        return ans;
    }
}
