class Solution:
    def maxOperations(self, s: str) -> int:
        total = 0 
        i = 0
        curr = 0
        while i < len(s) - 1:
            if s[i] == '1':
                curr += 1
            if s[i] == '1' and s[i + 1] == '0':
                total += curr
            i += 1
        return total
