class Solution {
    public int maxOperations(String s) {
        int st = 0;
        while(st<s.length() && s.charAt(st)==0){
            st++;
        }
        if(st>=s.length()-1){
            return 0;
        }
        int co = 0;
        int ans = 0;
        for(int i=st;i<s.length()-1;i++){
            if(s.charAt(i)=='1'){
                co++;
            }
            else{
                if(s.charAt(i+1)=='1'){
                    ans += co;
                }
            }
            //System.out.println(co+" "+ans);
        }
        if(s.charAt(s.length()-1)=='0'){
            ans += co;
        }
        return ans;
    }
}
