class Solution:
    def maxOperations(self, s: str) -> int:
        moves = 0

        groups = []

        for k, g in groupby(s):
            groups.append([k, len(list(g))])

        for i in range(len(groups) - 1):
            if groups[i][0] == '1' and groups[i + 1][0] == '0':
                moves += groups[i][1]
                if i + 2 < len(groups):
                    groups[i + 2][1] += groups[i][1]

        return moves
