class Solution:
    def maxOperations(self, s: str) -> int:
        nlist = []
        temp = 0
        for c in s :
            if c == '0' and temp > 0 :
                nlist.append(temp)
                temp = 0
            elif c == '1' :
                temp += 1
        # print(nlist)
        to_ret = 0
        for i, t in enumerate(nlist) :
            to_ret += t * (len(nlist) - i)
        return to_ret
