class Solution {
public:
    int maxOperations(string s) {
        int numberOf1 = 0;
        int count = 0;
        bool isLast1 = false;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '1') {
                numberOf1++;
                isLast1 = true;
            }
            else if (isLast1) {
                count += numberOf1;
                isLast1 = false;
            }
        }
        return count;
    }
};
