class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        n = len(s)
        count = [0] * n
        total = 0
        l = []
        for i in range(n):
            total += int(s[i])
            count[i] = total
            if i >= 1 and s[i] == "0" and s[i - 1] == "1":
                l.append(count[i])
        return sum(l) if l else 0
