class Solution:
    def maxOperations(self, s: str) -> int:
        ones = 0
        ans = 0

        s = s + '1'
        n = len(s)
        for i in range(n):
            if s[i] == '1':    
                if i > 0 and s[i-1] == '0':
                    ans += ones
                ones += 1   
            else:
                pass

        return ans
