class Solution {
public:
    int maxOperations(const string& s) {
        int n = s.size();
        int cOnes = 0, total = 0;
        bool gap = false;
        for(
            int i = 0; i < n; ++i
        ){
            if(s[i] == '1') {
                if (gap) total += cOnes;
                gap = false;
                cOnes++;
            }
            else{
                if(cOnes) gap = true;
            }
        }
        if (gap) total += cOnes;
        return total;
    }
};
