class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        vector<int>pre;
        int last=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                if(last==1){
                    pre.push_back(0);
                }
                pre.push_back(1);
                last=0;
            }
            else{
                last=1;
            }
        }
        if(last==1){
            pre.push_back(0);
        }
        int cnt=0;
        int ans=0;
        for(int i=pre.size()-1;i>=0;i--){
           if(pre[i]==0){
            cnt++;
           }else{
              ans+=cnt;
           }
        }
        return ans;
    }
};
