class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int i=0,cnt=0,ans=0;
        while(i<n){
            if(s[i]=='1') break;
            i++;
        }
        while(i<n and s[i]=='1'){
            cnt++; i++;
        }
        while(i<n){
            if(s[i]=='0'){
                ans+=cnt;
                while(i<n and s[i]=='0') i++;
                while(i<n and s[i]=='1'){
                    cnt++; i++;
                }
            } else i++;
        }
        return ans;
    }
};
