class Solution:
    def maxOperations(self, s: str) -> int:
        occ=[]
        for i in range(len(s)):
            if(s[i] == '1'):
                occ.append(i)
        size=1
        ans=0
        for i in range(1, len(occ)):
            dist=occ[i]-occ[i-1]
            if(dist > 1):
                ans+=size
            size+=1
        if(len(occ) > 0 and occ[-1] < len(s) - 1):
            ans+=size
        return ans
