class Solution {
public:
    int maxOperations(string s) {
        int c = 0;
        int res = 0;
        int n = s.size();
        for(int i = n - 1; i >= 0; --i) {
            if(s[i] == '0') {
                if(i + 1 == n || s[i + 1] == '1') {
                    c += 1;
                }
            } else {
                res += c;
            }
        }
        return res;
    }
};
