class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int i=0;
        int ans=0,ct=0;
        while(i<n){
            while(i<n && s[i]=='1'){
                i++;
                ct++;
            }
            int f=0;
            while(i<n && s[i]=='0'){
                if(f==0){
                    ans+=ct;
                    f=1;
                }
                i++;
            }
        }
        return ans;
    }
};
