class Solution:
    def maxOperations(self, s: str) -> int:
        cnt = 0
        ones = []
        flag = False
        for i,c in enumerate(s):
            if c=='1':
                if(flag):
                    cnt+=len(ones)
                ones.append(i)
                flag = False
            else:
                flag=True
        if(flag):cnt+=len(ones)
        return cnt
