class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> counts1;
        for(int i = 0; i < n; i++){
            if(s[i] == '1'){
                counts1.push_back(i);
            }
        }
        counts1.push_back(n);

        n = counts1.size();
        int count = 0;
        int result = 0;
        for(int i = n-1; i > 0; i--){
            if(counts1[i] - counts1[i-1] == 1){
                result += count;
            }
            else{
                ++count;
                result += count;
            }
        }
        return result;
    }
};
