class Solution:
    def maxOperations(self, s: str) -> int:
        ones = 0
        ans = 0
        i=0
        while i<len(s):
            c = s[i]
            if c=='1':
                ones += 1
            elif ones>0:
                ans += ones
                while i+1<len(s) and s[i+1]=='0':
                    i += 1

            i+=1

        return ans
