class Solution {
    public int maxOperations(String S) {
        char[] s = S.toCharArray();
        int n  =s.length;
        int l = 0, r = 0;
        int ans = 0;
        int c = 0;
        while (r < n) {
            l = r;
            while (r < n && s[r] == '1') {
                r++;
                c++;
            }
            l = r;
            while (r < n && s[r] == '0') {
                r++;
            }
            ans += c * Math.min(r - l, 1);
        }
        return ans;
    }
}
