class Solution {
public:
    int maxOperations(string s) {
        s += '1';
        int segs = 0;
        long long ans = 0;
        for(int i=s.size()-2; i>=0; i--){
            if(s[i] == '0' && s[i+1] == '1')
                segs ++;
            if(s[i] == '1')
                ans += segs;
        }
        return ans;
    }
};
