class Solution {
public:
    int maxOperations(string s) {
        int zeroes = 0, ans = 0;
        bool lastIsZero = false;
        reverse(s.begin(), s.end());
        for (auto c : s) {
            if (c == '0') {
                if (!lastIsZero) zeroes++;
                lastIsZero = true;
            }
            else {
                ans += zeroes;
                lastIsZero = false;
            }
        }
        return ans;
    }
};
