class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int cnt = 0;
        char a[] = s.toCharArray();
        if(a[0]=='1') cnt++;
        //System.out.println(cnt);
        for(int i=1;i<s.length();i++){
            if(a[i]=='1'){
                if(a[i-1]=='0') ans+=cnt;
                cnt++;
            }
            //System.out.println(cnt+" "+ans);
        }
        if(a[s.length()-1]=='0') ans+=cnt;
        return ans;
    }
}
