class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int res = 0, cnt = s[n - 1]=='0';
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '0') {
                if (s[i + 1] == '1') cnt++;
            } else {
                res += cnt;
            }
        }

        return res;
    }
};
