class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        vector<int>v;
        int curr=0;
        for(int i=0;i<n;i++){
            if(s[i] == '1')curr++;
            else if(s[i] == '0'){
                if(curr > 0){
                    v.push_back(curr);
                    curr=0;
                }
            }
        }
        if(curr > 0){
            v.push_back(curr);
            curr=0;
        }
        if(v.size() < 1){
            return 0;
        }

        int ans=0;
        int pre=v[0];
        for(int i=1;i<v.size();i++){
            ans+=pre;
            pre+=v[i];
        }

        if(s[n-1] == '0'){
            ans+=pre;
        }

        return ans;

    }
};
