class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int cntr=0;
        int idx=n-1;
        bool flag=true;
        while(idx>=0&&s[idx]=='1') idx-=1;
        for(int i=idx;i>=0;i--){
            if(s[i]=='0'){
                if(flag) cntr+=1;
                flag=false;
                continue;
            }
            flag=true;
            ans+=cntr;
        }
        return ans;
    }
};
