class Solution {
public:
    int maxOperations(string s) {
        int opCount = 0;
        int curOne = s[0] == '1';
        for (int i = 1; i < s.size(); ++i) {
            if (s[i] == '1') {
                if (s[i-1] == '0') opCount += curOne;
                curOne += 1;
            }
        }
        return opCount + ((s.back() == '0') ? curOne : 0);
    }
};
