class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int n = s.size();
        int t=0;
        for(int i=0;i<n-1;i++){
            if(s[i] == '1')t++;
            if(s[i]=='0' && s[i+1] == '1'){
                ans += t;
            }
        }
        if(s[n-1] == '0')ans+= t;
        return ans;
    }
};
